import React, { useRef } from 'react';
import Input from './App';

const App = () => {
    const inputRef = useRef(null);
    return (
        <div>
            <Input onFocus={() => inputRef.current.focus()}
                ref={inputRef} />
        </div>
    );
};

export default App;
